import React, {useMemo, useState} from "react";
import Counter from "./components/Conter"
import ClassCounter from "./components/ClassCounter";
import './styles/App.css';
import PostItem from "./components/PostItem";
import PostList from "./components/PostList";
import ButtonOne from "./components/UI/button/ButtonOne";
import MyInputOne from "./components/UI/input/MyInputOne";
import PostForm from "./components/PostForm";
import MySelectOne from "./components/UI/select/MySelectOne";
import PostFilter from "./components/PostFilter";
import MyModal from "./components/UI/MyModal/MyModal";

function App() {
    const [post, setPost] = useState([
        {id: 1, title: 'React 1', description: 'BLearn react for 7 days'},
        {id: 2, title: 'React 2', description: 'ALearn react for 7 days'},
        {id: 3, title: 'React 3', description: 'CLearn react for 7 days'}
    ])

    const [filter, setFilter] = useState({sort: '', query: ''})

    const createPost = (newPost) => {
        setPost([...post, newPost])
    }

    //useMemo - хук (визивається тільки тоді коли одна з залежностей міняється)
    const sortedPost = useMemo(() => {
        if(filter.sort){
            return [...post].sort((a, b) => a[filter.sort].localeCompare(b[filter.sort]))
        }
        return post;
    }, [filter.sort, post])

    const sortedAndSearchedPosts = useMemo(() => {
        return sortedPost.filter(post => post.title.toLowerCase().includes(filter.query))
    }, [filter.query, sortedPost])

    // получаємо пост з дочірнього елемента
    const removePost = (delPost) => {
        setPost(post.filter(p => p.id !== delPost.id))
    }

    return (
        <div className="App">
            <MyModal
                visible={true}
            >
                <PostForm create={createPost}/>
            </MyModal>
            <hr style={{margin: '15px'}}/>
            <PostFilter
                filter={filter}
                setFilter={setFilter}
            />
            <PostList remove={removePost} post={sortedAndSearchedPosts} title='Пости про JS'/>
        </div>
    );
};

export default App;
