import React, {useState} from "react";

function App() {

  const [likes, setLikes] = useState(5)
  const [value, setValue] = useState("sometext")

  function incr(){
    setLikes(likes + 1)
  }
  function decr(){
    setLikes(likes - 1)
  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <button
        onClick={incr}
      >
        Increment
      </button>
      <button
        onClick={decr}
      >
        Decrement
      </button>
      <h1>
      {value}
      </h1>
      <input
        type = "text"
        value = {value}
        onChange = {event => setValue(event.target.value)}
      />
    </div>
  );
}

export default App;
