import React, {useState} from "react";

const Counter = function () {

  const [likes, setLikes] = useState(0)
  function incr(){
    setLikes(likes + 1)
  }
  function decr(){
    setLikes(likes - 1)
  }
  return (
    <div>
      <h1>{likes}</h1>
      <button onClick={incr} >Increment</button>
      <button onClick={decr} >Decrement</button>
    </div>
  )
}
export default Counter;
