import {React, useState } from "react";

function App() {

  const [likes, setLikes] = useState(5)

  function incr(){
    setLikes(likes += 1)
  }
  function decr(){
    setLikes(likes -= 1)
  }
  return (
    <div className="App">
      <h1>{likes}</h1>
      <button
        onClick={incr}
      >
        Increment
      </button>
      <button
        onClick={decr}
      >
        Decrement
      </button>
    </div>
  );
}

export default App;
