import React, {useState} from "react";
import Counter from "./components/Conter"
import ClassCounter from "./components/ClassCounter";
import './styles/App.css';
import PostItem from "./components/PostItem";
import PostList from "./components/PostList";
import ButtonOne from "./components/UI/button/ButtonOne";
import MyInputOne from "./components/UI/input/MyInputOne";

function App() {
    const [post, setPost] = useState([
        {id: 1, title: 'React 1', description: 'Learn react for 7 days'},
        {id: 2, title: 'React 2', description: 'Learn react for 7 days'},
        {id: 3, title: 'React 3', description: 'Learn react for 7 days'}
    ])

    const [onePost, setOnePost] = useState({title: '', description: ''})
    const addNewPost = (e) => {
        // Функція, яка заберає перезагрузку сторінки
        e.preventDefault()
        const newPost = {
            id: Date.now(),
            title,
            description
        }
        console.log(newPost)
        setPost([...post, newPost])
        setOnePost({title: '', description: ''})
    }
    return (
        <div className="App">
            <form>
                {/*Керований елемент*/}
                <MyInputOne
                    value={onePost.title}
                    onChange={e => setOnePost({...onePost, title: e.target.value})}
                    type='text'
                    placeholder='назва'
                />
                {/*Некерований елемент*/}
                <MyInputOne
                    value={onePost.description}
                    onChange={e => setOnePost({...onePost, description: e.target.value})}
                    type='text'
                    placeholder='опис'
                />
                <ButtonOne onClick={addNewPost} >Додати</ButtonOne>
            </form>
            <PostList post={post} title='Пости про JS'/>
        </div>
    );
};

export default App;
