        {id: 2, title: 'React 2', description: 'Learn react for 7 days'},
        {id: 3, title: 'React 3', description: 'Learn react for 7 days'}
    ])
  return (
    <div className="App">
        <form>
            <input type='text' placeholder='назва'/>
            <input type='text' placeholder='опис'/>
            <ButtonOnen>Додати</ButtonOnen>
        </form>
        <PostList post={post} title='Пости про JS'/>
    </div>
  );
};

export default App;
